import React, { useState } from 'react';
import AdminPage from './components/AdminPage';
import PublicCheckIn from './components/PublicCheckIn';
import './App.css';

function App() {
  const [waitingList, setWaitingList] = useState([]);
  const [isLightMode, setIsLightMode] = useState(false);
  const [currentView, setCurrentView] = useState('public'); // 'public' or 'admin'
  const [calledClient, setCalledClient] = useState(null);

  const addToWaitingList = (clientData) => {
    const newClient = {
      id: Date.now(),
      ...clientData,
      checkInTime: new Date().toLocaleString(),
      status: 'waiting'
    };
    setWaitingList(prev => [...prev, newClient]);
  };

  const removeFromWaitingList = (id) => {
    setWaitingList(prev => prev.filter(client => client.id !== id));
  };

  const updateClientStatus = (id, status) => {
    setWaitingList(prev => 
      prev.map(client => 
        client.id === id ? { ...client, status } : client
      )
    );
  };

  const callClient = (id) => {
    const client = waitingList.find(c => c.id === id);
    if (client) {
      setCalledClient(client);
      updateClientStatus(id, 'called');
      // Show call notification for 5 seconds
      setTimeout(() => {
        setCalledClient(null);
      }, 5000);
    }
  };

  const getPublicWaitingList = () => {
    return waitingList.filter(client => client.status !== 'completed');
  };

  if (currentView === 'admin') {
    return (
      <AdminPage
        waitingList={waitingList}
        onRemove={removeFromWaitingList}
        onUpdateStatus={updateClientStatus}
        onCallClient={callClient}
        isLightMode={isLightMode}
        onBackToPublic={() => setCurrentView('public')}
      />
    );
  }

  return (
    <div className={`app ${isLightMode ? 'light-mode' : ''}`}>
      <header className="app-header">
        <div className="header-content">
          <h1>Check-in Waiting List</h1>
          <div className="header-actions">
            <button
              className="admin-toggle"
              onClick={() => setCurrentView('admin')}
              title="Admin Dashboard"
            >
              <Settings size={20} />
              <span>Admin</span>
            </button>
            <ThemeToggle isLightMode={isLightMode} setIsLightMode={setIsLightMode} />
          </div>
        </div>
      </header>
      
      {/* Call Notification */}
      {calledClient && (
        <div className="call-notification">
          <div className="call-content">
            <h3>ðŸ“¢ Please come to Accounting Room</h3>
            <p>{calledClient.name} - {calledClient.phone}</p>
          </div>
        </div>
      )}
      
      <main className="app-main">
        <div className="container">
          <CheckInForm onAddToWaitingList={addToWaitingList} isLightMode={isLightMode} />
          <WaitingList 
            waitingList={getPublicWaitingList()} 
            onRemove={removeFromWaitingList}
            onUpdateStatus={updateClientStatus}
            isLightMode={isLightMode}
          />
        </div>
      </main>
    </div>
  );
}

export default App;
